import styled from "styled-components";
import { FaReact } from "react-icons/fa";

const size = {
  mobileS: "320px",
  mobileM: "375px",
  mobileL: "425px",
  tablet: "768px",
  laptop: "1024px",
  laptopL: "1440px",
  desktop: "2560px"
};

const device = {
  mobileS: `(min-width: ${size.mobileS})`,
  mobileM: `(min-width: ${size.mobileM})`,
  mobileL: `(min-width: ${size.mobileL})`,
  tablet: `(min-width: ${size.tablet})`,
  laptop: `(min-width: ${size.laptop})`,
  laptopL: `(min-width: ${size.laptopL})`,
  desktop: `(min-width: ${size.desktop})`
};

export const UIContainer = styled.div`
	display: grid;
	grid-template-areas: 'Info' 'Projects';
	grid-template-rows: 25vh 36vh;
	grid-template-columns: 1fr;
  font-family: "Poppins", sans-serif;
	width: 100%;
	height: 100%;
  color: #eee;
`;

export const StyledInfoContainer = styled.div`
	font-size: 1.6em;
  display: grid;
	grid-area: Info;
	grid-template-columns: min-content 1fr max-content min-content min-content;
	grid-template-areas: "avatar . time reacticon battery";
  grid-column-gap: 0.9em;
	width: 91.15vw;
	height: 7vh;
	padding-top: 5vh;
	padding-left: 4.425vw;
`;

export const Avatar = styled.img`
  height: 2em;
  width: 2em;
  border: 0.13em solid #505050;
  border-radius: 50%;
  grid-area: avatar;
  text-align: center;
	text-justify: auto;
	box-shadow: 0px 0px 0.3em rgba(0, 0, 0, 0.4);
`;

export const CurrentTime = styled.div`
	flex-shrink: 0;
  display: flex;
  grid-area: time;
`;

export const Time = styled.div`
	flex-shrink: 0;
  align-self: center;
`;

export const PMAM = styled.div`
	flex-shrink: 0;
  align-self: center;
	margin-left: 0.4em;
  padding-top: 0.2em;
  font-size: 0.75em;
`;

export const StyledFaReact = styled(FaReact)`
  align-self: center;
  grid-area: reacticon;
`;

export const BatteryLife = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  grid-area: battery;
	width: 2em;
`;

export const BatteryLifeOuter = styled.div`
	position: absolute;
  height: 0.5em;
  width: 1.05em;
  border: 0.45vh solid #eee;
`;

export const BatteryLifeInner = styled.div`
	position: absolute;
  height: 0.4em;
  width: 0.9em;
  background-color: #eee;
`;

export const BatteryLifeTerminal = styled.div`
	position: absolute;
  height: 0.25em;
  width: 0.2em;
  background-color: #eee;
  margin-left: 1.3em;
`;
