import { useEffect, useState } from 'react'
import { FaReact } from "react-icons/fa";

import { UIContainer } from './styles/StyledInfoContainer'

import ProjectsContainer from './components/ProjectsContainer';
import { AppContainer } from './styles/AppContainer';

export default function App() {

	const [currentTime, setCurrentTime] = useState('')
  const [pmAm, setPmAm] = useState('')

  //init current active time
  useEffect(() => {
    const date = new Date();
    let hours: number = date.getHours();
    let minutes: any = date.getMinutes();
    if (hours === 12) {
      setPmAm('PM')
    } else if (hours > 11 && hours !== 12) {
      hours = date.getHours() - 12;
      setPmAm('PM')
    } else setPmAm('AM')
    if(minutes < 10) {minutes = `0${minutes}`}
    setCurrentTime(`${hours} : ${minutes}`)
    setCurrentTime(`4 : 15`)
  }, [])

  //update time every 30 seconds
  useEffect(() => {
    setInterval(() => {
      const date = new Date();
      let hours = date.getHours();
      let minutes: any = date.getMinutes();
      if (hours === 12) {
        setPmAm('PM')
      } else if (hours > 11) {
        hours = date.getHours() - 12;
        setPmAm('PM')
      } else setPmAm('AM')
      if (minutes < 10) minutes = `0${minutes}`
    setCurrentTime(`${hours} : ${minutes}`)
		setCurrentTime(`4 : 15`)
    }, 30000)
  }, [])

  const [isScrolling, setScrolling] = useState(false);
	const [scrollLeft, setScrollLeft] = useState(0);
	const [clientX, setClientX] = useState(0);

	// onMouseMove function
	const handleMouseMovement = (e: React.MouseEvent<HTMLDivElement, MouseEvent>) => {
		if(isScrolling) {
			e.preventDefault()
			e.currentTarget.scrollLeft = scrollLeft - e.clientX + clientX;
		}
		//  = offsetLeft - posX + e.clientX;
	}

  function handleTileClick(linksrc: string) {
    window.open(linksrc, "_blank");
  }

  return (
		<div id='appContainer'>
			<div id='UIContainer'>
        <div className="infoContainer">
          <img className='avatar' src={require('../assets/avatar.png')} alt="Avatar" />
          <div className='currentTime'>
            <div className='time'>{currentTime}</div>
            <div className='pmam'> {pmAm}</div>
          </div>
          <FaReact id='faReact'/>
          <div className='batteryLife'>
            <div className='batteryLifeOuter'/>
            <div className='batteryLifeInner'/>
            <div className='batteryLifeTerminal'/>
          </div> 
        </div>
        <StyledProjectsContainer 
          onMouseDown={e => {setScrolling(true); setScrollLeft(e.currentTarget.scrollLeft); setClientX(e.clientX)}}
          onMouseUp={e => setScrolling(false)}
          onMouseMove={(e) => handleMouseMovement(e)}
        >
          <StyledProjectTile src={require("../assets/pokedex.webp")} alt={AppList[0].name} onClick={() => handleTileClick(AppList[0].link)} />
          <StyledProjectTile src={require("../assets/keyboardenthusiast.webp")} alt={AppList[1].name} onClick={() => handleTileClick(AppList[1].link)} />
          <StyledAllProjects id="All Projects">
            <SquareOne/>
            <SquareTwo/>
            <SquareThree/>
            <SquareFour/>
          </StyledAllProjects>
        </StyledProjectsContainer>
			</div>
		</div>
  );
}
