import { useState } from 'react'
import { StyledProjectsContainer } from '../styles/StyledProjectsContainer'
import { AllProjects } from './ProjectTile'
import { AppList } from "../assets/AppList"
import { StyledProjectTile } from '../styles/StyledProjectTile'

const ProjectsContainer = () => {

	// Maintain MouseDown location and scroll info onmousedown of window
	// Add mousemove listener onmousedown
	// Calculate scrollLeft according to window clientX in mousemove
	// Remove mousemove on window's onmouseup listener
	// algo = scrollLeft - clientX + event.clientX

	const [isScrolling, setScrolling] = useState(false);
	const [scrollLeft, setScrollLeft] = useState(0);
	const [clientX, setClientX] = useState(0);

	// onMouseMove function
	const handleMouseMovement = (e: React.MouseEvent<HTMLDivElement, MouseEvent>) => {
		if(isScrolling) {
			e.preventDefault()
			e.currentTarget.scrollLeft = scrollLeft - e.clientX + clientX;
		}
		//  = offsetLeft - posX + e.clientX;
	}

  function handleTileClick(linksrc: string) {
    window.open(linksrc, "_blank");
  }

	return (
		<StyledProjectsContainer 
			onMouseDown={e => {setScrolling(true); setScrollLeft(e.currentTarget.scrollLeft); setClientX(e.clientX)}}
			onMouseUp={e => setScrolling(false)}
			onMouseMove={(e) => handleMouseMovement(e)}
		>
      <StyledProjectTile src={require("../assets/pokedex.webp")} alt={AppList[0].name} onClick={() => handleTileClick(AppList[0].link)} />
			<AllProjects/>
		</StyledProjectsContainer>
	)
}

export default ProjectsContainer
